import React, { useContext, useEffect, useState, useCallback, useMemo } from 'react';
import { Link } from 'react-router-dom';
import axiosInstance from '../utils/axiosConfig';
import profileStyles from '../styles/pages/Profile.module.css';
import data from '../Context/ContextApi';
import { useAnimeContext } from '../Context/AnimeContext';
import { useMangaContext } from '../Context/MangaContext';
import { fetchWithErrorHandling } from '../utils/apiUtils';
import UserAnimeCard from '../cards/userAnimeCard';
import MangaCard from '../cards/MangaCard';

const Profile = () => {
  const { animeList } = useAnimeContext();
  const { mangaList } = useMangaContext();
  const { userData } = useContext(data);
  const [mediaType, setMediaType] = useState('anime');
  const [statusType, setStatusType] = useState('watching');
  const [userAnimeList, setUserAnimeList] = useState([]);
  const [userMangaList, setUserMangaList] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [gridLayout, setGridLayout] = useState('grid');
  const [error, setError] = useState(null);

  const changeLayout = (layout) => setGridLayout(layout);

  const fetchUserList = useCallback(async () => {
    try {
      setIsLoading(true);
      setError(null);
      const data = await fetchWithErrorHandling(`/users/${userData._id}/current`);
      setUserAnimeList(data.animes);
      setUserMangaList(data.mangas);
    } catch (error) {
      setError('Failed to load user list. Please try again later.');
      setUserAnimeList([]);
      setUserMangaList([]);
    } finally {
      setIsLoading(false);
    }
  }, [userData._id]);

  useEffect(() => {
    if (userData._id && animeList?.length && mangaList?.length) {
      fetchUserList();
    }
  }, [userData._id, animeList, mangaList, fetchUserList]);

  const getMediaById = useCallback((mediaId, type) => {
    return type === 'anime' 
      ? animeList?.find((anime) => anime._id === mediaId)
      : mangaList?.find((manga) => manga._id === mediaId);
  }, [animeList, mangaList]);

  const filteredMediaList = useMemo(() => {
    const list = mediaType === 'anime' ? userAnimeList : userMangaList;
    if (!list.length) return [];
    
    const statusMap = {
      'watching': mediaType === 'anime' ? 'Watching' : 'Reading',
      'planning': mediaType === 'anime' ? 'Plan to Watch' : 'Plan to Read',
      'completed': 'Completed'
    };
    
    return list
      .filter((item) => item.status === statusMap[statusType])
      .map((item) => ({
        ...item,
        mediaDetails: getMediaById(
          mediaType === 'anime' ? item.animeId : item.mangaId,
          mediaType
        ),
      }));
  }, [userAnimeList, userMangaList, statusType, getMediaById, mediaType]);

  const handleProgressUpdate = async (id, newProgress) => {
    const isAnime = mediaType === 'anime';
    const currentList = isAnime ? userAnimeList : userMangaList;
    const currentItem = currentList.find(item => 
      isAnime ? item.animeId === id : item.mangaId === id
    );

    if (!currentItem) return;

    const updateList = isAnime ? setUserAnimeList : setUserMangaList;
    const progressField = isAnime ? 'currentEpisode' : 'currentChapter';

    try {
      updateList(prevList =>
        prevList.map(item =>
          (isAnime ? item.animeId : item.mangaId) === id
            ? { ...item, [progressField]: newProgress }
            : item
        )
      );

      const endpoint = isAnime 
        ? `/users/${userData._id}/updateAnime`
        : `/users/${userData._id}/updateManga`;

      await axiosInstance.post(endpoint, {
        [isAnime ? 'animeId' : 'mangaId']: id,
        status: currentItem.status,
        [progressField]: newProgress,
        ...(isAnime ? {} : { currentVolume: currentItem.currentVolume })
      });
    } catch (error) {
      setError(`Failed to update ${mediaType} progress. Please try again.`);
      // Revert the optimistic update
      fetchUserList();
    }
  };

  const getTitle = (titles) => {
    const preference = userData.title || 'english';
    const titleMap = {
      'english': titles.english,
      'romaji': titles.romaji,
      'native': titles.native
    };
    return titleMap[preference] || titles.english || titles.romaji || titles.native;
  };

  const renderLayoutControls = () => (
    <div className={profileStyles.layoutControls}>
      <button onClick={() => changeLayout('default')} className={profileStyles.layoutButton}>Default</button>
      <button onClick={() => changeLayout('wide')} className={profileStyles.layoutButton}>Wide</button>
      <button onClick={() => changeLayout('compact')} className={profileStyles.layoutButton}>Compact</button>
    </div>
  );

  return (
    <div className={profileStyles.profilePage}>
      <div className={profileStyles.profileHeader}>
        <div className={profileStyles.userInfo}>
          <div className={profileStyles.avatarContainer}>
            <div className={profileStyles.avatar}>
              {userData.username?.charAt(0).toUpperCase()}
            </div>
          </div>
          <div className={profileStyles.userDetails}>
            <h1>{userData.username}</h1>
            <p>Member since {new Date(userData.createdAt).toLocaleDateString()}</p>
          </div>
        </div>
        <div className={profileStyles.statsContainer}>
          <div className={profileStyles.statCard}>
            <h3>Anime Stats</h3>
            <div className={profileStyles.statValue}>
              {userData.animes?.length || 0} Total
            </div>
          </div>
          <div className={profileStyles.statCard}>
            <h3>Manga Stats</h3>
            <div className={profileStyles.statValue}>
              {userData.mangas?.length || 0} Total
            </div>
          </div>
        </div>
      </div>

      <div className={profileStyles.contentSection}>
        <div className={profileStyles.controls}>
          <div className={profileStyles.tabContainer}>
            <button
              className={`${profileStyles.tabButton} ${mediaType === 'anime' ? profileStyles.activeTab : ''}`}
              onClick={() => setMediaType('anime')}
            >
              Anime
            </button>
            <button
              className={`${profileStyles.tabButton} ${mediaType === 'manga' ? profileStyles.activeTab : ''}`}
              onClick={() => setMediaType('manga')}
            >
              Manga
            </button>
          </div>

          {renderLayoutControls()}
        </div>

        <div className={profileStyles.statusTabs}>
          <button
            className={`${profileStyles.statusTab} ${statusType === 'watching' ? profileStyles.activeStatus : ''}`}
            onClick={() => setStatusType('watching')}
          >
            {mediaType === 'anime' ? 'Watching' : 'Reading'}
          </button>
          <button
            className={`${profileStyles.statusTab} ${statusType === 'planning' ? profileStyles.activeStatus : ''}`}
            onClick={() => setStatusType('planning')}
          >
            Planning
          </button>
          <button
            className={`${profileStyles.statusTab} ${statusType === 'completed' ? profileStyles.activeStatus : ''}`}
            onClick={() => setStatusType('completed')}
          >
            Completed
          </button>
        </div>

        {error && (
          <div className={profileStyles.error}>
            {error}
          </div>
        )}

        {isLoading ? (
          <div className={profileStyles.loading}>Loading...</div>
        ) : (
          <div className={`${profileStyles.contentGrid} ${profileStyles[gridLayout]}`}>
            {filteredMediaList.map((item) => (
              mediaType === 'anime' ? (
                <UserAnimeCard
                  key={item.animeId}
                  anime={item.mediaDetails}
                  name={getTitle(item.mediaDetails.titles)}
                  layout={gridLayout}
                  userProgress={item.currentEpisode}
                  onProgressUpdate={(newProgress) => 
                    handleProgressUpdate(item.animeId, newProgress)
                  }
                />
              ) : (
                <MangaCard
                  key={item.mangaId}
                  manga={item.mediaDetails}
                  name={getTitle(item.mediaDetails.titles)}
                  layout={gridLayout}
                  userProgress={item.currentChapter}
                  onProgressUpdate={(newProgress) => 
                    handleProgressUpdate(item.mangaId, newProgress)
                  }
                />
              )
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
