import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import cardsStyles from '../styles/components/userCards.module.css';

function UserAnimeCard({
  anime,
  name,
  layout = 'grid', // 'grid', 'list', or 'compact'
  userProgress,
  onProgressUpdate,
}) {
  const [isHovered, setIsHovered] = useState(false);
  const [titleHeight, setTitleHeight] = useState('auto');
  const titleRef = useRef(null);

  useEffect(() => {
    if (titleRef.current) {
      const height = titleRef.current.scrollHeight;
      setTitleHeight(height);
    }
  }, [anime.titles.english]);

  const renderStatusDot = () => (
    <div className={cardsStyles.statusDot} />
  );

  const renderProgress = () => {
    const total = anime.lengths.Episodes || '?';
    return `${userProgress}/${total}`;
  };

  if (layout === 'list') {
    return (
      <div className={cardsStyles.listItem}>
        <div className={cardsStyles.listItemContent}>
          {renderStatusDot()}
          <div className={cardsStyles.coverImage}>
            <img src={anime.images.image} alt={name} className={cardsStyles.smallCover} />
          </div>
          <div className={cardsStyles.titleSection}>
            <Link to={`/anime/${anime._id}`} className={cardsStyles.titleLink}>
              {name}
            </Link>
          </div>
          <div className={cardsStyles.scoreSection}>
            {renderProgress()}
          </div>
          <div className={cardsStyles.typeSection}>
            {anime.typings.Format}
          </div>
        </div>
      </div>
    );
  }

  if (layout === 'compact') {
    return (
      <div className={cardsStyles.compactItem}>
        {renderStatusDot()}
        <Link to={`/anime/${anime._id}`} className={cardsStyles.compactLink}>
          <div className={cardsStyles.compactContent}>
            <span className={cardsStyles.compactTitle}>{name}</span>
            <span className={cardsStyles.compactProgress}>{renderProgress()}</span>
            <span className={cardsStyles.compactType}>{anime.typings.Format}</span>
          </div>
        </Link>
      </div>
    );
  }

  // Grid layout (default)
  return (
    <div 
      className={cardsStyles.gridCard}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {renderStatusDot()}
      <Link to={`/anime/${anime._id}`} className={cardsStyles.gridLink}>
        <div className={cardsStyles.coverWrapper}>
          <img 
            src={anime.images.image} 
            alt={name} 
            className={cardsStyles.coverImage}
          />
          <div className={cardsStyles.overlay}>
            <div className={cardsStyles.progressOverlay}>
              {renderProgress()}
            </div>
          </div>
        </div>
        <div className={cardsStyles.cardInfo}>
          <h3 className={cardsStyles.cardTitle} ref={titleRef}>
            {name}
          </h3>
          <div className={cardsStyles.cardMeta}>
            {anime.typings.Format}
          </div>
        </div>
      </Link>
      {isHovered && onProgressUpdate && (
        <button
          className={cardsStyles.incrementButton}
          onClick={(e) => {
            e.preventDefault();
            e.stopPropagation();
            onProgressUpdate(userProgress + 1);
          }}
        >
          +
        </button>
      )}
    </div>
  );
}

export default UserAnimeCard;
