import React, { useContext, useEffect, useState, useCallback, useMemo } from 'react';
import { Link } from 'react-router-dom';
import profileStyles from '../styles/pages/Profile.module.css';
import cardsStyles from '../styles/components/cards.module.css';
import data from '../Context/ContextApi';
import AnimeCard from '../cards/AnimeCard';
import MangaCard from '../cards/MangaCard';
import { useAnimeContext } from '../Context/AnimeContext';
import { useMangaContext } from '../Context/MangaContext';
import { fetchWithErrorHandling } from '../utils/apiUtils';

const Profile = () => {
  const { animeList } = useAnimeContext();
  const { mangaList } = useMangaContext();
  const { userData } = useContext(data);
  const [activeTab, setActiveTab] = useState('anime');
  const [userAnimeList, setUserAnimeList] = useState([]);
  const [userMangaList, setUserMangaList] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const fetchUserList = useCallback(async () => {
    try {
      setIsLoading(true);
      const data = await fetchWithErrorHandling(`/users/${userData._id}/current`);
      setUserAnimeList(data.animes);
      setUserMangaList(data.mangas);
    } catch (error) {
      setUserAnimeList([]);
      setUserMangaList([]);
    } finally {
      setIsLoading(false);
    }
  }, [userData._id]);

  useEffect(() => {
    if (userData._id && animeList?.length && mangaList?.length) {
      fetchUserList();
    }
  }, [userData._id, animeList, mangaList, fetchUserList]);

  const getAnimeById = useCallback((animeId) => {
    return animeList?.find((anime) => anime._id === animeId);
  }, [animeList]);

  const getMangaById = useCallback((mangaId) => {
    return mangaList?.find((manga) => manga._id === mangaId);
  }, [mangaList]);

  const watchingAnime = useMemo(() => {
    if (!animeList?.length) return [];
    
    return userAnimeList
      .filter((userAnime) => userAnime.status === 'Watching')
      .map((userAnime) => ({
        animeId: userAnime.animeId,
        currentEpisode: userAnime.currentEpisode,
        status: userAnime.status,
        animeDetails: getAnimeById(userAnime.animeId),
      }));
  }, [userAnimeList, getAnimeById, animeList]);

  const readingManga = useMemo(() => {
    if (!mangaList?.length) return [];
    
    return userMangaList
      .filter((userManga) => userManga.status === 'Reading')
      .map((userManga) => ({
        mangaId: userManga.mangaId,
        currentChapter: userManga.currentChapter,
        currentVolume: userManga.currentVolume,
        status: userManga.status,
        mangaDetails: getMangaById(userManga.mangaId),
      }));
  }, [userMangaList, getMangaById, mangaList]);

  const animeTitle = useCallback((titles) => {
    switch (userData.title) {
      case 'english':
        return titles.english || titles.romaji;
      case 'romaji':
        return titles.romaji || titles.english;
      case 'native':
        return titles.native;
      default:
        return titles.english || titles.romaji || titles.native || 'Unknown Title';
    }
  }, [userData.title]);

  const mangaTitle = useCallback((titles) => {
    switch (userData.title) {
      case 'english':
        return titles.english || titles.romaji;
      case 'romaji':
        return titles.romaji || titles.english;
      case 'native':
        return titles.native;
      default:
        return titles.english || titles.romaji || titles.native || 'Unknown Title';
    }
  }, [userData.title]);

  const renderUserStats = () => (
    <div className={profileStyles.statsContainer}>
      <div className={profileStyles.statCard}>
        <h3>Anime Stats</h3>
        <div className={profileStyles.statValue}>
          {userData.animes?.length || 0} Total
        </div>
      </div>
      <div className={profileStyles.statCard}>
        <h3>Manga Stats</h3>
        <div className={profileStyles.statValue}>
          {userData.mangas?.length || 0} Total
        </div>
      </div>
      <div className={profileStyles.statCard}>
        <h3>Member Since</h3>
        <div className={profileStyles.statValue}>
          {new Date(userData.createdAt).toLocaleDateString()}
        </div>
      </div>
    </div>
  );

  const renderContent = () => {
    if (isLoading || !animeList?.length || !mangaList?.length) {
      return <div>Loading...</div>;
    }

    if (activeTab === 'anime') {
      return (
        <div className={profileStyles.contentGrid}>
          {watchingAnime.map((anime) => (
            anime.animeDetails && (
              <AnimeCard
                key={anime.animeId}
                anime={anime}
                name={animeTitle(anime.animeDetails.titles)}
                layout="compact"
                hideTopRightButton={true}
              />
            )
          ))}
        </div>
      );
    }

    return (
      <div className={profileStyles.contentGrid}>
        {readingManga.map((manga) => (
          manga.mangaDetails && (
            <MangaCard
              key={manga.mangaId}
              manga={manga}
              name={mangaTitle(manga.mangaDetails.titles)}
              layout="compact"
              hideTopRightButton={true}
            />
          )
        ))}
      </div>
    );
  };

  return (
    <div className={profileStyles.profilePage}>
      <div className={profileStyles.profileHeader}>
        <div className={profileStyles.userInfo}>
          <div className={profileStyles.avatarContainer}>
            <div className={profileStyles.avatar}>
              {userData.username?.charAt(0).toUpperCase()}
            </div>
          </div>
          <div className={profileStyles.userDetails}>
            <h1>{userData.username}</h1>
            <p>Member since {new Date(userData.createdAt).toLocaleDateString()}</p>
          </div>
        </div>
        {renderUserStats()}
      </div>

      <div className={profileStyles.contentSection}>
        <div className={profileStyles.tabContainer}>
          <button
            className={`${profileStyles.tabButton} ${activeTab === 'anime' ? profileStyles.activeTab : ''}`}
            onClick={() => setActiveTab('anime')}
          >
            Anime List
          </button>
          <button
            className={`${profileStyles.tabButton} ${activeTab === 'manga' ? profileStyles.activeTab : ''}`}
            onClick={() => setActiveTab('manga')}
          >
            Manga List
          </button>
        </div>

        {renderContent()}
      </div>
    </div>
  );
};

export default Profile;
